import React from 'react'
import { MapTo } from '@adobe/aem-react-editable-components';
import styled from 'styled-components';
import PropTypes from 'prop-types';

const Title = styled.h1`
    font-family: 'Poppins';
    font-style: normal;
    font-weight: 400;
    font-size: 60px;
    line-height: 90px;
    /* color: #e0e0e0; */
    color: ${props => props.titleColor};

  @media (max-width: 650px) {
    font-size: 48px;
    line-height: 0px;
  }
`;
const Paragraph = styled.p`
    font-family: 'Poppins';
    font-style: normal;
    font-weight: 400;
    font-size: 16px;
    line-height: 24px;
    color: #e0e0e0;
    color: ${props => props.paragraphColor};
`;


export const TitleSection = ({ title, paragraph, titleColor, paragraphColor }) => {
    return (
        <div>
            <Title titleColor={titleColor} >{title}</Title>
            <Paragraph paragraphColor={paragraphColor}>{paragraph}</Paragraph>
        </div>
    )
}

TitleSection.propTypes = {
    titleColor: PropTypes.string,
    title: PropTypes.string,
    paragraph: PropTypes.string,
}

export default MapTo('reactapp/components/title-section')(TitleSection);
