import React, { useState } from 'react'
import { useHistory } from "react-router-dom";
import { MapTo } from '@adobe/aem-react-editable-components';
require('./index.css')

function initialState() {
    return { user: '', password: '' };
}

function login(user, password) {
    if (user === 'admin' && password === 'admin') {
        return { token: '1234' }
    }
    return { error: 'Usuário ou senha inválido' }
}

export const LoginForm = ({ title, buttonName }) => {

    const [values, setValues] = useState()
    const history = useHistory();

    function onChange(event) {
        const { value, name } = event.target;

        setValues({
            ...values,
            [name]: value,
        });
    }

    function onSubmit(event) {
        event.preventDefault();

        

    }

    const routeChange = () => {
        let path = "/content/reactapp/us/en/homepage.html";
        history.push(path);

    }

    return (
        <form onSubmit={onSubmit}>
            <h1 className="form-title">{title}</h1>
            <div className="input-container">
                <input
                    className="form-input"
                    id="user"
                    type="text"
                    name="user"
                    placeholder="Usuário"
                    onChange={onChange}
                    value={values.user}
                />
                <input
                    className="form-input"
                    id="password"
                    type="password"
                    name="password"
                    placeholder="Senha"
                    onChange={onChange}
                    value={values.password}
                />
            </div>

            <button className="form-button" onClick={routeChange} type="submit" >
                {buttonName}
            </button>

        </form>
    )
}
export default MapTo('reactapp/components/form-section')(LoginForm);
