import React, { useState } from 'react'
import { useHistory } from "react-router-dom";
import { MapTo } from '@adobe/aem-react-editable-components';
require('./index.css')

function initialState() {
    return { user: '', password: '' };
}

export const LoginForm = ({ title, buttonName }) => {

    const [values, setValues] = useState()
    const history = useHistory();

    function onChange(event) {
        event.preventDefault();
    }

    const routeChange = () => {
        let path = "/content/reactapp/us/en/homepage.html";
        history.push(path);

    }




    return (
        <form action="/" method="post">
            <h1 className="form-title">{title}</h1>
            <div className="input-container">
                <input
                    className="form-input"
                    type="text"
                    name="uname" 
                    placeholder="UsuÃ¡rio"
                />
                <input
                    className="form-input"
                    type="password"
                    placeholder="Senha"
                    name="psw"
                />
            </div>

            <button className="form-button" onClick={routeChange} type="submit" >
                {buttonName}
            </button>

        </form>
    )
}
export default MapTo('reactapp/components/form-section')(LoginForm);
