import React from 'react';
require('./index.css')

export const BodySection = ({text, title, image = {}}) => {

    return (
        <div className="divContainer">
            <p className="paragraph">{text}</p>
            <p className="paragraph2">{title}</p>
            <img src={image} alt="img" />
        </div>
    );
};

export default BodySection;
