import React from 'react'
import { MapTo } from '@adobe/aem-react-editable-components';

export const LoginForm = ({title, buttonName}) => {
    return (
        <form action="/" method="post">
            <h1>{title}</h1>
            <div className="Container">
                <input type="text" placeholder="UsuÃ¡rio" name="uname" required />
            </div>

            <div className="Container">
                <input type="password" placeholder="Senha" name="psw" required />
            </div>

            <button>{buttonName}</button>
        </form>
    )
}
export default MapTo('reactapp/components/form-section')(LoginForm);
